    31→/**
    32→ * Service for managing MCP server configurations (SDK-aligned)
    33→ * @see https://docs.anthropic.com/en/api/agent-sdk/mcp
    34→ */
    35→export class MCPService {
    36→  private logger: Logger;
    37→  private sdkServers: Map<string, any> = new Map();
    38→
    39→  constructor() {
    40→    this.logger = createLogger('MCPService');
    41→  }
    42→
    43→  /**
    44→   * Get project MCP config path (.mcp.json at project root)
    45→   * SDK-aligned location
    46→   */
    47→  private getMCPConfigPath(projectPath: string): string {
    48→    return path.join(projectPath, '.mcp.json');
    49→  }
    50→
    51→  /**
    52→   * Check for legacy config locations and auto-migrate if found
    53→   */
    54→  private async checkAndMigrateLegacyConfigs(projectPath: string): Promise<void> {
    55→    const legacyPath = path.join(projectPath, '.claude', 'mcp.json');
    56→
    57→    if (!existsSync(legacyPath)) {
    58→      return; // No legacy config, nothing to do
    59→    }
    60→
    61→    try {
    62→      // Read legacy config
    63→      const legacyConfig = await this.readMCPConfig(legacyPath);
    64→      if (!legacyConfig || !legacyConfig.mcpServers || Object.keys(legacyConfig.mcpServers).length === 0) {
    65→        // Empty legacy config, just delete it
    66→        await fs.unlink(legacyPath);
    67→        this.logger.info('Removed empty legacy MCP config', { legacyPath });
    68→        return;
    69→      }
    70→
    71→      // Get current config path
    72→      const newConfigPath = this.getMCPConfigPath(projectPath);
    73→
    74→      // Read existing new config
    75→      let newConfig = await this.readMCPConfig(newConfigPath);
    76→
    77→      if (!newConfig) {
    78→        // No new config exists, just move the legacy one
    79→        await this.writeMCPConfig(newConfigPath, legacyConfig);
    80→        await fs.unlink(legacyPath);
    81→        this.logger.info(
    82→          '✓ Migrated legacy MCP config to .mcp.json',
    83→          {
    84→            from: legacyPath,
    85→            to: newConfigPath,
    86→            serverCount: Object.keys(legacyConfig.mcpServers).length
    87→          }
    88→        );
    89→      } else {
    90→        // Both configs exist, merge them (new config takes precedence)
    91→        const mergedServers = {
    92→          ...legacyConfig.mcpServers,
    93→          ...newConfig.mcpServers, // New config overwrites legacy
    94→        };
    95→
    96→        const mergedConfig: MCPConfig = {
    97→          mcpServers: mergedServers,
    98→        };
    99→
   100→        await this.writeMCPConfig(newConfigPath, mergedConfig);
   101→        await fs.unlink(legacyPath);
   102→        this.logger.info(
   103→          '✓ Merged and migrated legacy MCP config to .mcp.json',
   104→          {
   105→            from: legacyPath,
   106→            to: newConfigPath,
   107→            totalServers: Object.keys(mergedServers).length
   108→          }
   109→        );
   110→      }
   111→    } catch (error) {
   112→      this.logger.error(
   113→        'Failed to migrate legacy MCP config. Please manually move .claude/mcp.json to .mcp.json',
   114→        { legacyPath, error }
   115→      );
   116→    }
   117→  }
