   119→  /**
   120→   * Read MCP config from file with env var substitution
   121→   */
   122→  private async readMCPConfig(configPath: string): Promise<MCPConfig | null> {
   123→    try {
   124→      if (!existsSync(configPath)) {
   125→        return null;
   126→      }
   127→
   128→      const content = await fs.readFile(configPath, 'utf-8');
   129→      let config = JSON.parse(content) as MCPConfig;
   130→
   131→      // Apply environment variable substitution
   132→      config = substituteEnvVarsInObject(config);
   133→
   134→      return config;
   135→    } catch (error) {
   136→      this.logger.error('Failed to read MCP config', { path: configPath, error });
   137→      return null;
   138→    }
   139→  }
   140→
   141→  /**
   142→   * Write MCP config to file
   143→   */
   144→  private async writeMCPConfig(configPath: string, config: MCPConfig): Promise<void> {
   145→    try {
   146→      // Ensure directory exists
   147→      const dir = path.dirname(configPath);
   148→      await fs.mkdir(dir, { recursive: true });
   149→
   150→      // Write config
   151→      await fs.writeFile(configPath, JSON.stringify(config, null, 2), 'utf-8');
   152→
   153→      this.logger.info('MCP config written', { path: configPath });
   154→    } catch (error) {
   155→      this.logger.error('Failed to write MCP config', { path: configPath, error });
   156→      throw error;
   157→    }
   158→  }
   159→
   160→  /**
   161→   * Convert MCP config entries to MCPServer objects
   162→   */
   163→  private configToServers(config: MCPConfig | null): MCPServerInternal[] {
   164→    if (!config || !config.mcpServers) {
