   198→  /**
   199→   * Get all MCP servers from project config
   200→   */
   201→  async getMCPServers(projectPath?: string): Promise<MCPServerInternal[]> {
   202→    if (!projectPath) {
   203→      projectPath = process.cwd();
   204→    }
   205→
   206→    // Auto-migrate legacy configs if found
