    32→/**
    33→ * Validates skill name format
    34→ */
    35→function validateSkillName(name: string): string | null {
    36→  if (!name || name.trim().length === 0) {
    37→    return 'Skill name is required';
    38→  }
    39→
    40→  if (name.length > 64) {
    41→    return 'Skill name must be 64 characters or less';
    42→  }
    43→
    44→  if (!/^[a-z0-9-]+$/.test(name)) {
    45→    return 'Skill name must contain only lowercase letters, numbers, and hyphens';
    46→  }
    47→
    48→  return null;
    49→}
    50→
    51→/**
    52→ * Validates skill description
    53→ */
    54→function validateSkillDescription(description: string): string | null {
    55→  if (!description || description.trim().length === 0) {
    56→    return 'Skill description is required';
    57→  }
    58→
    59→  if (description.length > 1024) {
    60→    return 'Skill description must be 1024 characters or less';
    61→  }
    62→
    63→  if (!description.toLowerCase().includes('use when')) {
    64→    return 'Skill description should include "Use when..." to describe when to use this skill';
    65→  }
    66→
    67→  return null;
    68→}
    69→
    70→/**
    71→ * Creates YAML frontmatter for SKILL.md
    72→ */
    73→function createSkillFrontmatter(request: CreateSkillRequest): string {
    74→  const frontmatter = ['---', `name: ${request.name}`, `description: ${request.description}`];
    75→
    76→  if (request.allowedTools && request.allowedTools.length > 0) {
    77→    frontmatter.push(`allowed-tools: ${request.allowedTools.join(', ')}`);
    78→  }
    79→
    80→  // Add MCP tools if present
    81→  if (request.mcpTools && Object.keys(request.mcpTools).length > 0) {
    82→    frontmatter.push('mcp_tools:');
    83→    Object.entries(request.mcpTools).forEach(([serverId, tools]) => {
    84→      if (Array.isArray(tools) && tools.length > 0) {
    85→        frontmatter.push(`  ${serverId}:`);
    86→        tools.forEach((toolName: string) => {
    87→          frontmatter.push(`    - ${toolName}`);
    88→        });
    89→      }
    90→    });
    91→  }
    92→
    93→  frontmatter.push('---');
    94→
    95→  return frontmatter.join('\n');
    96→}
