    98→/**
    99→ * Creates a new skill in the .claude/skills/ directory
   100→ */
   101→export async function createSkill(
   102→  request: CreateSkillRequest,
   103→  projectRoot: string
   104→): Promise<CreateSkillResponse> {
   105→  try {
   106→    // Validate name
   107→    const nameError = validateSkillName(request.name);
   108→    if (nameError) {
   109→      return { success: false, error: nameError };
   110→    }
   111→
   112→    // Validate description
   113→    const descError = validateSkillDescription(request.description);
   114→    if (descError) {
   115→      return { success: false, error: descError };
   116→    }
   117→
   118→    // Validate content
   119→    if (!request.content || request.content.trim().length === 0) {
   120→      return { success: false, error: 'Skill content/instructions are required' };
   121→    }
   122→
   123→    // Determine skill directory path
   124→    const skillsDir = path.join(projectRoot, '.claude', 'skills');
   125→    const skillDir = path.join(skillsDir, request.name);
   126→    const skillMdPath = path.join(skillDir, 'SKILL.md');
   127→
   128→    // Check if skill already exists
   129→    if (existsSync(skillDir)) {
   130→      return {
   131→        success: false,
   132→        error: `Skill "${request.name}" already exists at ${skillDir}`,
   133→      };
   134→    }
   135→
   136→    // Create skills directory if it doesn't exist
   137→    await fs.mkdir(skillDir, { recursive: true });
   138→
   139→    // Create SKILL.md content
   140→    const frontmatter = createSkillFrontmatter(request);
   141→    const skillContent = `${frontmatter}\n\n${request.content}`;
   142→
   143→    // Write SKILL.md file
   144→    await fs.writeFile(skillMdPath, skillContent, 'utf-8');
   145→
   146→    // Write skill.config.json if inputFields are provided
   147→    if (request.inputFields && request.inputFields.length > 0) {
   148→      const configPath = path.join(skillDir, 'skill.config.json');
   149→      const configData = {
   150→        inputs: request.inputFields,
   151→      };
   152→      await fs.writeFile(configPath, JSON.stringify(configData, null, 2), 'utf-8');
   153→    }
   154→
   155→    // Parse and return the created skill
   156→    const skill: Skill = {
   157→      id: request.name,
   158→      name: request.name,
   159→      description: request.description,
   160→      path: skillDir,
   161→      skillMdPath,
   162→      content: request.content,
   163→      metadata: (request.allowedTools || request.mcpTools || request.inputFields)
   164→        ? {
   165→            ...(request.allowedTools ? { allowedTools: request.allowedTools } : {}),
   166→            ...(request.mcpTools ? { mcpTools: request.mcpTools } : {}),
   167→            ...(request.inputFields ? { inputFields: request.inputFields } : {}),
   168→          }
   169→        : undefined,
   170→    };
   171→
   172→    return {
   173→      success: true,
   174→      skill,
   175→      path: skillMdPath,
   176→    };
   177→  } catch (error) {
   178→    console.error('Error creating skill:', error);
   179→    return {
   180→      success: false,
   181→      error: error instanceof Error ? error.message : 'Unknown error occurred',
   182→    };
   183→  }
   184→}
